                    <h3 className="text-xl font-semibold mt-2 group-hover:text-primary transition-colors">
                      {activity.title}
                    </h3>
                  </div>
                </div>
              </div>

              <div className="space-y-3 mb-6">
                <div className="flex items-center text-muted-foreground">
                  <MapPin className="w-4 h-4 mr-2" />
